import { useEffect, useState } from 'react';
import axios from 'axios';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend } from 'recharts';

function RealTime() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:8001/api/fleet/realtime')
      .then(res => {
        setData(res.data);
        setLoading(false);
      });
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-6 text-center">ðŸ“Š Real-Time Fleet Data</h2>

      {loading ? (
        <p className="text-center">Loading data...</p>
      ) : (
        <>
          {/* Table View */}
          <div className="overflow-x-auto mb-8">
            <table className="min-w-full table-auto border border-gray-200 shadow-md rounded-lg">
              <thead className="bg-gray-100">
                <tr>
                  <th className="p-3 text-left">Route</th>
                  <th className="p-3 text-left">Vehicle</th>
                  <th className="p-3 text-left">Speed (km/h)</th>
                  <th className="p-3 text-left">Total Cost (â‚¹)</th>
                </tr>
              </thead>
              <tbody>
                {data.map((d, i) => (
                  <tr key={i} className="border-t hover:bg-gray-50">
                    <td className="p-3">{d.route.sourceCity} â†’ {d.route.destinationCity}</td>
                    <td className="p-3">{d.vehicle.registrationNumber}</td>
                    <td className="p-3">{d.averageSpeedKmph.toFixed(1)}</td>
                    <td className="p-3">â‚¹{d.totalCost.toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Bar Chart */}
          <div className="h-80">
            <ResponsiveContainer width="100%" height="100%">
              <BarChart data={data} margin={{ top: 20, right: 30, left: 0, bottom: 5 }}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="vehicle.registrationNumber" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="averageSpeedKmph" fill="#3182ce" name="Speed (km/h)" />
                <Bar dataKey="totalCost" fill="#38a169" name="Cost (â‚¹)" />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </>
      )}
    </div>
  );
}

export default RealTime;
