import { useEffect, useState } from 'react';
import axios from 'axios';

function RealTime() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:8001/api/fleet/realtime')
      .then(res => {
        setData(res.data);
        setLoading(false);
      });
  }, []);

  return (
    <div
      className="min-h-screen bg-cover bg-center flex items-center justify-center"
      style={{
        backgroundImage: 'url("https://images.unsplash.com/photo-1504384308090-c894fdcc538d?auto=format&fit=crop&w=1400&q=80")'
      }}
    >
      <div className="bg-white bg-opacity-95 rounded-lg shadow-lg w-11/12 h-[90vh] overflow-auto p-6">
        <h2 className="text-3xl font-bold text-center mb-6 text-gray-800">
          ðŸš› Real-Time Fleet Data
        </h2>

        {loading ? (
          <p className="text-center text-lg">Loading data...</p>
        ) : (
          <table className="w-full text-left text-base border border-gray-300">
            <thead className="bg-gray-100 text-gray-700">
              <tr>
                <th className="border border-gray-300 px-4 py-3">Route</th>
                <th className="border border-gray-300 px-4 py-3">Vehicle</th>
                <th className="border border-gray-300 px-4 py-3">Speed (km/h)</th>
                <th className="border border-gray-300 px-4 py-3">Total Cost (â‚¹)</th>
              </tr>
            </thead>
            <tbody>
              {data.map((d, i) => (
                <tr key={i} className="hover:bg-gray-50">
                  <td className="border border-gray-300 px-4 py-3">
                    {d.route.sourceCity} â†’ {d.route.destinationCity}
                  </td>
                  <td className="border border-gray-300 px-4 py-3">
                    {d.vehicle.registrationNumber}
                  </td>
                  <td className="border border-gray-300 px-4 py-3">
                    {d.averageSpeedKmph.toFixed(1)}
                  </td>
                  <td className="border border-gray-300 px-4 py-3">
                    â‚¹{d.totalCost.toFixed(2)}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default RealTime;
