import { useEffect, useState } from 'react';
import axios from 'axios';

function CostAnalysis() {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/fleet/cost-analysis')
      .then(res => setData(res.data));
  }, []);

  return (
    <div>
      <h2>Cost Analysis</h2>
      {data.map((d, i) => (
        <div key={i}>
          <p>Route: {d.route.sourceCity} → {d.route.destinationCity}</p>
          <p>Fuel Cost: ₹{d.totalFuelCost.toFixed(2)}</p>
          <p>Maintenance: ₹{d.totalMaintenanceCost.toFixed(2)}</p>
          <p>Total: ₹{d.totalCost.toFixed(2)} | Avg Speed: {d.avgSpeed.toFixed(1)} km/h</p>
        </div>
      ))}
    </div>
  );
}
export default CostAnalysis;
