import { useEffect, useState } from 'react';
import axios from 'axios';

function RealTime() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:8001/api/fleet/realtime')
      .then(res => {
        setData(res.data);
        setLoading(false);
      });
  }, []);

  return (
    <div
      className="w-screen h-screen bg-cover bg-center flex items-center justify-center"
      style={{
        backgroundImage: 'url("https://images.unsplash.com/photo-1504384308090-c894fdcc538d?auto=format&fit=crop&w=1600&q=80")',
      }}
    >
      <div className="bg-white bg-opacity-90 rounded-lg shadow-xl w-11/12 max-w-6xl p-8 overflow-auto">
        <h2 className="text-3xl font-bold text-center mb-6 text-gray-800">
          ðŸš› Real-Time Fleet Data
        </h2>

        {loading ? (
          <p className="text-center text-lg text-gray-600">Loading data...</p>
        ) : (
          <div className="overflow-auto max-h-[70vh]">
            <table className="w-full table-fixed border-collapse bg-white text-gray-800">
              <thead className="bg-gray-100">
                <tr>
                  <th className="border border-gray-300 px-4 py-3 w-1/4">Route</th>
                  <th className="border border-gray-300 px-4 py-3 w-1/4">Vehicle</th>
                  <th className="border border-gray-300 px-4 py-3 w-1/4">Speed (km/h)</th>
                  <th className="border border-gray-300 px-4 py-3 w-1/4">Total Cost (â‚¹)</th>
                </tr>
              </thead>
              <tbody>
                {data.map((d, i) => (
                  <tr key={i} className="hover:bg-gray-50">
                    <td className="border border-gray-300 px-4 py-3">
                      {d.route.sourceCity} â†’ {d.route.destinationCity}
                    </td>
                    <td className="border border-gray-300 px-4 py-3">{d.vehicle.registrationNumber}</td>
                    <td className="border border-gray-300 px-4 py-3">{d.averageSpeedKmph.toFixed(1)}</td>
                    <td className="border border-gray-300 px-4 py-3">â‚¹{d.totalCost.toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default RealTime;
